{"changed":true,"filter":false,"title":"naranjero.rb","tooltip":"/lib/pract05/naranjero.rb","value":"#encoding: utf-8\nrequire \"pract05/version\"\n\nmodule Pract05\n    class Naranjero\n        attr_accessor :edad, :altura, :contador, :vive, :naranjas\n        \n        def initialize\n            @edad = 0\n            @altura = 0\n            @contador = 0\n            @vive = true\n            @naranjas = []\n        end\n        \n        def set_edad(edad)\n            @edad = edad\n            if edad >= 30\n                @vive = false\n            end\n        end\n        \n        def uno_mas(i)\n            j = 0\n            cont = 0\n            mutex = Mutex.new\n            while j < i\n                if @edad >= 30\n                    @vive = false\n                    puts \"El árbol ha muerto\"\n                else\n                    @edad += 1\n                    @vive = true\n                    if altura.to_i < 3\n                        @altura += 0.5\n                    else\n                        @altura += 0.2\n                    end\n                    if @edad > 3\n                        mutex.lock\n                        n = 2 * @edad\n                        @contador += n\n                        @naranjas << n\n                        puts \"El árbol ha dado #{@naranjas[cont]} naranjas\"\n                        mutex.unlock\n                        cont += 1\n                    else\n                        puts \"El árbol es muy joven, aún no da naranjas\"\n                    end\n                end\n                j += 1\n                sleep(1)\n            end\n        end\n        \n        def recolectar_una(i)\n            puts \"\\n\"\n            j = 0\n            mutex = Mutex.new\n            while j < i\n                if @vive == true\n                    if contador > 0\n                        mutex.lock\n                        @contador -= 1\n                        j += 1\n                        mutex.unlock\n                        puts \"La naranja estaba deliciosa, quedan #{contador} naranjas\"\n                    else\n                        puts \"No hay naranjas\"\n                        sleep(2)\n                    end\n                else\n                    puts \"El árbol está muerto\"\n                end\n                sleep(1)\n            end\n        end\n    end\nend","undoManager":{"mark":99,"position":100,"stack":[[{"group":"doc","deltas":[{"start":{"row":14,"column":15},"end":{"row":14,"column":16},"action":"insert","lines":["_"]}]}],[{"group":"doc","deltas":[{"start":{"row":14,"column":16},"end":{"row":14,"column":17},"action":"insert","lines":["e"]}]}],[{"group":"doc","deltas":[{"start":{"row":14,"column":17},"end":{"row":14,"column":18},"action":"insert","lines":["d"]}]}],[{"group":"doc","deltas":[{"start":{"row":14,"column":18},"end":{"row":14,"column":19},"action":"insert","lines":["a"]}]}],[{"group":"doc","deltas":[{"start":{"row":14,"column":19},"end":{"row":14,"column":20},"action":"insert","lines":["d"]}]}],[{"group":"doc","deltas":[{"start":{"row":14,"column":20},"end":{"row":14,"column":22},"action":"insert","lines":["()"]}]}],[{"group":"doc","deltas":[{"start":{"row":14,"column":21},"end":{"row":14,"column":22},"action":"insert","lines":["e"]}]}],[{"group":"doc","deltas":[{"start":{"row":14,"column":22},"end":{"row":14,"column":23},"action":"insert","lines":["d"]}]}],[{"group":"doc","deltas":[{"start":{"row":14,"column":23},"end":{"row":14,"column":24},"action":"insert","lines":["a"]}]}],[{"group":"doc","deltas":[{"start":{"row":14,"column":24},"end":{"row":14,"column":25},"action":"insert","lines":["d"]}]}],[{"group":"doc","deltas":[{"start":{"row":14,"column":26},"end":{"row":15,"column":0},"action":"insert","lines":["",""]},{"start":{"row":15,"column":0},"end":{"row":15,"column":12},"action":"insert","lines":["            "]}]}],[{"group":"doc","deltas":[{"start":{"row":15,"column":12},"end":{"row":15,"column":13}